mapper.select:
/* 관리자 리스트 조회 */ SELECT `ADMIN_IDX`, `ADMIN_ID`, `ADMIN_NM`, `ADMIN_EMAIL`, `USE_YN`, `DESC`, `REG_DTTM`, `REG_ADMIN_IDX`, `UPD_DTTM`, `UPD_ADMIN_IDX`, `CMPLX_ID`, IF( `CMPLX_ID` = NULL, '-', (SELECT `CMPLX_NM` FROM COMPLEX_M WHERE COMPLEX_M.CMPLX_ID = A.CMPLX_ID) ) AS `CMPLX_NM`, `GRP_ID`, `GRP_NM`, ROW_NUMBER() OVER(ORDER BY GRP_ID, ADMIN_ID ASC) AS RNUM, COUNT(1) OVER() AS TOT FROM( SELECT `ADMIN_IDX`, `ADMIN_ID`, `ADMIN_NM`, `ADMIN_EMAIL`, `USE_YN`, `A_I`.`DESC` AS `DESC`, `A_I`.`REG_DTTM` AS `REG_DTTM`, `REG_ADMIN_IDX`, `UPD_DTTM`, `UPD_ADMIN_IDX`, `CMPLX_ID`, `A_G`.`GRP_ID` AS `GRP_ID`, `A_G`.`GRP_NM` AS `GRP_NM` FROM CL_ADMIN_INFO AS A_I LEFT JOIN CL_ADMIN_GRP AS A_G ON A_I.GRP_ID = A_G.GRP_ID WHERE 1=1
AND A_I.CMPLX_ID = #{searchKeyword1}
AND A_I.GRP_ID LIKE #{searchKeyword1}
AND A_I.ADMIN_ID LIKE CONCAT('%', #{searchKeyword1}, '%')
AND A_I.ADMIN_NM LIKE CONCAT('%', #{searchKeyword1}, '%')
AND (A_I.ADMIN_ID LIKE CONCAT('%', #{searchKeyword1}, '%') OR A_I.ADMIN_NM LIKE CONCAT('%', #{searchKeyword1}, '%'))
)A ORDER BY RNUM
