mapper.select:
/* 프로퍼티 리스트 조회 */ SELECT PROP_GROUP, PROP_KEY, PROP_VALUE, PROP_DESC, USE_YN, REG_DTTM, REG_USERID, UPD_DTTM, UPD_USERID FROM SERVICE_PROPERTIES WHERE IFNULL(USE_YN,'Y') = 'Y'
AND PROP_GROUP LIKE '%'+#{propGroup}+'%'
AND PROP_KEY LIKE '%'+#{propKey}+'%'
;

mapper.update:
/* 프로퍼티 수정 */

UPDATE SYS_PROPERTIES SET



    PROP_VALUE = #{propValue},



    PROP_DESC = #{propDesc},



    USE_YN = #{useYn},



    UPD_DTTM = CONVERT(DATETIME, GETDATE(), 121),
    UPD_USERID = #{updUserId}
WHERE PROP_GROUP = #{propGroup}
  AND PROP_KEY = #{propKey}
